export default class Timer{constructor(t=10){this.data=[],this.maxDataLength=t}start(){this.startTime=Date.now()}reset(){this.startTime=0}stop(){if(this.startTime<=0)return;const t=Date.now()-this.startTime;this.reset(),this.data.push(t),this.data.length>this.maxDataLength&&this.data.shift()}avg(){return this.data.length>0?Math.round(this.data.reduce(((t,a)=>t+a))/this.data.length):0}}
